import React, { useEffect, useRef } from 'react';

export default function UnityGame() {
  const unityContainer = useRef(null);

  useEffect(() => {
    // Make sure your build files are in public/Build
    const script = document.createElement('script');
    script.src = '/Build/UnityLoader.js'; // Path to UnityLoader.js
    script.onload = () => {
      // eslint-disable-next-line no-undef
      UnityLoader.instantiate(
        unityContainer.current,
        '/Build/YourGame.json', // Path to your game JSON
        {
          onProgress: (instance, progress) => {
            console.log('Loading Unity WebGL: ', Math.round(progress * 100) + '%');
          },
        }
      );
    };
    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div
      ref={unityContainer}
      style={{ width: '960px', height: '600px', margin: 'auto' }}
    />
  );
}
