import React, { useEffect, useRef, useState } from 'react';

export default function UnityGame() {
  const unityContainer = useRef(null);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    // For older Unity versions that use UnityLoader.js
    const script = document.createElement('script');
    script.src = '/assets/Build/UnityLoader.js'; // <-- absolute path
    script.onload = () => {
      // eslint-disable-next-line no-undef
      UnityLoader.instantiate(
        unityContainer.current,
        '/assets/Build/YourGame.json', // <-- absolute path
        {
          onProgress: (instance, p) => {
            setProgress(p);
            console.log('Loading Unity WebGL:', Math.round(p * 100) + '%');
          },
        }
      );
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div style={{ width: '100%', maxWidth: '960px', margin: 'auto' }}>
      {progress < 1 && (
        <div style={{ color: '#aaa', textAlign: 'center', marginBottom: '8px' }}>
          Loading {Math.round(progress * 100)}%
        </div>
      )}

      <div
        ref={unityContainer}
        style={{
          width: '100%',
          height: '60vh',
          background: '#000',
          borderRadius: '12px',
        }}
      />
    </div>
  );
}
