import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment, Html, useGLTF } from '@react-three/drei';

function Model() {
  // Replace the path below with the correct relative path to your GLB in public/models/
  const gltf = useGLTF('/models/lab-model.glb');
  return <primitive object={gltf.scene} scale={1} position={[0, 0, 0]} />;
}

export default function LabModel() {
  return (
    <div className="w-full h-[500px] bg-gray-800 rounded-xl overflow-hidden shadow-lg">
      <Canvas camera={{ position: [0, 2, 5], fov: 50 }}>
        {/* Basic lighting */}
        <ambientLight intensity={0.5} />
        <directionalLight position={[5, 5, 5]} intensity={1} />

        {/* Suspense ensures model loads before rendering */}
        <Suspense
          fallback={
            <Html center>
              <div className="text-white">Loading 3D Model...</div>
            </Html>
          }
        >
          <Model />
        </Suspense>

        {/* OrbitControls for interactivity */}
        <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />
        {/* Optional HDRI environment for reflections */}
        <Environment preset="sunset" />
      </Canvas>
    </div>
  );
}
