import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';
import { Box } from '@react-three/drei';

function Model({ url }) {
  try {
    const gltf = useGLTF(url);
    return <primitive object={gltf.scene} />;
  } catch (err) {
    console.warn('Failed to load GLB, showing fallback cube', err);
    return <Box args={[2, 2, 2]}>
      <meshStandardMaterial color="red" />
    </Box>;
  }
}

export default function LabModel() {
  return (
    <div className="w-full h-[500px]">
      <Canvas>
        <ambientLight intensity={0.5} />
        <directionalLight position={[5, 5, 5]} intensity={1} />
        <Suspense fallback={<Box args={[2, 2, 2]}><meshStandardMaterial color="gray" /></Box>}>
          <Model url="/assets/models/lab-model.glb" />
        </Suspense>
        <OrbitControls />
      </Canvas>
    </div>
  );
}
