import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Html, useGLTF } from '@react-three/drei';

function Model() {
  // Make sure this path matches your public folder: public/models/lab-model.glb
  const gltf = useGLTF('./models/lab-model.glb');
  return <primitive object={gltf.scene} scale={1} />;
}

export default function LabModel() {
  return (
    <Canvas style={{ width: '100%', height: '500px' }}>
      {/* Lights */}
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} intensity={1} />

      {/* Suspense handles loading */}
      <Suspense fallback={<Html><span style={{ color: 'white' }}>Loading 3D Model...</span></Html>}>
        <Model />
      </Suspense>

      {/* User controls to rotate/pan/zoom */}
      <OrbitControls />
    </Canvas>
  );
}
