import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

// Try to load the GLB, fallback to a red cube if it fails
function Model() {
  try {
    const { scene } = useGLTF('/models/lab-model.glb');
    return <primitive object={scene} scale={1} position={[0, -0.5, 0]} />;
  } catch (err) {
    console.warn('Failed to load GLB, showing fallback cube', err);
    return (
      <mesh position={[0, 0, 0]}>
        <boxGeometry args={[1, 1, 1]} />
        <meshStandardMaterial color="red" />
      </mesh>
    );
  }
}

export default function LabModel() {
  return (
    <div className="w-full h-[500px] rounded-xl overflow-hidden bg-gray-900 shadow-xl">
      <Canvas camera={{ position: [0, 1, 3], fov: 50 }}>
        {/* Lights */}
        <ambientLight intensity={0.8} />
        <directionalLight position={[3, 3, 3]} intensity={1} />
        <directionalLight position={[-3, 2, -3]} intensity={0.5} />

        {/* Load 3D model with fallback */}
        <Suspense
          fallback={
            <mesh>
              <boxGeometry args={[1, 1, 1]} />
              <meshStandardMaterial color="gray" />
            </mesh>
          }
        >
          <Model />
        </Suspense>

        {/* Camera controls */}
        <OrbitControls enablePan={false} />
      </Canvas>
    </div>
  );
}
