// LabModel.js
import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, Environment, Html, useProgress } from '@react-three/drei';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { useLoader } from '@react-three/fiber';
import { Mesh } from 'three';

// Loader component for showing progress
function Loader() {
  const { progress } = useProgress();
  return (
    <Html center>
      <div style={{ color: 'white', fontSize: '14px' }}>Loading: {progress.toFixed(0)}%</div>
    </Html>
  );
}

// Component to load the GLB
function Model({ path }) {
  let gltf;
  try {
    gltf = useLoader(GLTFLoader, path);
  } catch (err) {
    console.error('GLB Load failed, using fallback cube:', err);
    return (
      <mesh>
        <boxGeometry args={[1, 1, 1]} />
        <meshStandardMaterial color="red" />
      </mesh>
    );
  }

  return <primitive object={gltf.scene} scale={1} />;
}

export default function LabModel() {
  return (
    <Canvas
      camera={{ position: [2, 2, 2], fov: 50 }}
      style={{ width: '100%', height: '500px', background: '#111' }}
    >
      <ambientLight intensity={0.6} />
      <directionalLight position={[5, 5, 5]} intensity={1} />
      <Suspense fallback={<Loader />}>
        <Model path="/assets/models/lab-model.glb" />
        <Environment preset="city" />
      </Suspense>
      <OrbitControls enablePan={true} enableZoom={true} enableRotate={true} />
    </Canvas>
  );
}
