import React, { useState, useEffect } from 'react';
import { HashRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { motion, AnimatePresence } from 'framer-motion';

import WorkWithUs from './WorkWithUs';
import AboutUs from './AboutUs';
import TheLab from './TheLab';

import ISText from './assets/IronpulseText.png';
import FBIcon from './assets/AstroAssault.png';
import PBS from './assets/ProjectBreakerScreen1.png';
import PFantom from './assets/ProjectPhantom.png';
import BTreas from './assets/BrowserTreasure.png';
import PFAlien from './assets/ProjectFallaceAlien.png';

function AnimatedRoutes({ children }) {
  const location = useLocation();
  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        {children}
      </Routes>
    </AnimatePresence>
  );
}

// =================== SUPPORTERS CAROUSEL ===================
const supporters = [
  { id: 's1', logo: 'https://via.placeholder.com/150x80?text=Supporter+1', name: 'Supporter 1' },
  { id: 's2', logo: 'https://via.placeholder.com/150x80?text=Supporter+2', name: 'Supporter 2' },
  { id: 's3', logo: 'https://via.placeholder.com/150x80?text=Supporter+3', name: 'Supporter 3' },
  { id: 's4', logo: 'https://via.placeholder.com/150x80?text=Supporter+4', name: 'Supporter 4' },
  { id: 's5', logo: 'https://via.placeholder.com/150x80?text=Supporter+5', name: 'Supporter 5' },
];

function SupportersCarousel() {
  const [currentIndex, setCurrentIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentIndex((prev) => (prev + 1) % supporters.length);
    }, 3000);
    return () => clearInterval(interval);
  }, []);

  const visibleCount = 4;
  const displayed = [];
  for (let i = 0; i < visibleCount; i++) {
    displayed.push(supporters[(currentIndex + i) % supporters.length]);
  }

  return (
    <div className="overflow-hidden w-full py-8 bg-gray-900">
      <motion.div 
        className="flex gap-8 w-max"
        animate={{ x: -currentIndex * 200 }} // 200px per item
        transition={{ type: 'spring', stiffness: 50, damping: 20 }}
      >
        {supporters.map((s) => (
          <div key={s.id} className="flex-shrink-0 w-36 h-20 flex items-center justify-center bg-gray-800 rounded-lg shadow-lg">
            <img src={s.logo} alt={s.name} className="max-h-16 object-contain" />
          </div>
        ))}
      </motion.div>
    </div>
  );
}

// =================== MAIN APP ===================
export default function App() {
  const [activeGame, setActiveGame] = useState(null);
  const [currentSlide, setCurrentSlide] = useState(0);
  const [menuOpen, setMenuOpen] = useState(false);

  const mainCarousel = [
    {
      id: 'breaker',
      title: 'PROJECT BREAKER',
      cover: FBIcon,
      splashImages: [PBS, PBS, PBS],
      platforms: ['IOS', 'Android', 'Itch.io'],
      release: '2026',
      status: 'In Development',
    },
    {
      id: 'fallace',
      title: 'PROJECT FALLACE',
      cover: PFAlien,
      splashImages: [PFAlien, PFAlien, PFAlien],
      platforms: ['PC'],
      release: '2028',
