import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const MODEL_URL = `${process.env.PUBLIC_URL}/assets/models/lab-model.glb`;

useGLTF.preload(MODEL_URL);

function Model() {
  const gltf = useGLTF(MODEL_URL);
  return <primitive object={gltf.scene} scale={0.6} position={[0, -0.5, 0]} />;
}

export default function LabModel() {
  return (
    <div className="w-full h-96 rounded-xl overflow-hidden border border-gray-800">
      <Canvas camera={{ position: [0, 1.5, 3], fov: 50 }}>
        <ambientLight intensity={0.4} />
        <directionalLight position={[5, 5, 5]} intensity={1.2} />
        <directionalLight position={[-5, 3, -5]} intensity={0.6} />

        <Suspense fallback={<Loading />}>
          <Model />
        </Suspense>

        <OrbitControls enableZoom enablePan={false} />
      </Canvas>
    </div>
  );
}

function Loading() {
  return (
    <mesh>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial wireframe />
    </mesh>
  );
}
