// LabModel.js
import React, { useEffect, useRef, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';

function Model({ path }) {
  const ref = useRef();
  const [gltfScene, setGltfScene] = useState(null);
  const [error, setError] = useState(false);

  useEffect(() => {
    const loader = new GLTFLoader();

    loader.load(
      path,
      (gltf) => {
        setGltfScene(gltf.scene); // set loaded model
      },
      undefined,
      (err) => {
        console.error('Failed to load GLB:', err);
        setError(true); // fallback to cube
      }
    );
  }, [path]);

  if (error) {
    // Fallback red cube
    return (
      <mesh>
        <boxGeometry args={[1, 1, 1]} />
        <meshStandardMaterial color="red" />
      </mesh>
    );
  }

  // Render the GLB if loaded
  return gltfScene ? <primitive object={gltfScene} ref={ref} /> : null;
}

export default function LabModel() {
  return (
    <Canvas style={{ width: '100%', height: '500px', background: '#111' }}>
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} />
      <Model path="/assets/models/lab-model.glb" />
      <OrbitControls />
    </Canvas>
  );
}
