import React, { useEffect, useState } from 'react';

export default function UnityGame() {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const base = process.env.PUBLIC_URL || '';

    const script = document.createElement('script');
    script.src = `${base}/assets/Build/assets.loader.js`;
    script.async = true;

    script.onload = () => {
      if (!window.createUnityInstance) {
        console.error('❌ Unity loader loaded, but createUnityInstance not found');
        return;
      }

      window.createUnityInstance(
        document.querySelector('#unity-container'), // ✅ selector string
        {
          dataUrl: `${base}/assets/Build/assets.data`,
          frameworkUrl: `${base}/assets/Build/assets.framework.js`,
          codeUrl: `${base}/assets/Build/assets.wasm`,
          streamingAssetsUrl: `${base}/StreamingAssets`,
          companyName: 'Iron Shift',
          productName: 'The Lab',
          productVersion: '1.0',
        },
        (p) => {
          setProgress(p);
          console.log('Loading Unity WebGL:', Math.round(p * 100) + '%');
        }
      );
    };

    script.onerror = () => {
      console.error('❌ Failed to load Unity loader script');
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div style={{ width: '100%', maxWidth: '1100px', margin: 'auto' }}>
      {progress < 1 && (
        <div style={{ color: '#aaa', textAlign: 'center', marginBottom: '8px' }}>
          Loading {Math.round(progress * 100)}%
        </div>
      )}

      {/* Unity mount point */}
      <div
        id="unity-container"   // ✅ Unity selector target
        style={{
          width: '100%',
          height: '60vh',
          background: '#000',
          borderRadius: '12px',
          overflow: 'hidden',
        }}
      />
    </div>
  );
}
