// LabModel.jsx
import React, { Suspense, useRef, useState, useEffect } from 'react';
import { Canvas, useThree, useFrame } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';
import { Box3, Vector3 } from 'three';

const MODEL_URL = `${process.env.PUBLIC_URL}/assets/models/lab-model.glb`;
useGLTF.preload(MODEL_URL);

// ----------------------------
// Loading fallback cube
function Loading() {
  return (
    <mesh>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial wireframe />
    </mesh>
  );
}

// ----------------------------
// Model component with centering and camera focus
function Model({ controlsRef }) {
  const gltf = useGLTF(MODEL_URL);
  const ref = useRef();
  const { camera } = useThree();

  useEffect(() => {
    if (!ref.current) return;

    const box = new Box3().setFromObject(ref.current);
    const center = new Vector3();
    const size = new Vector3();

    box.getCenter(center);
    box.getSize(size);

    // Center model at origin
    ref.current.position.sub(center);

    // Auto-scale model
    const maxDim = Math.max(size.x, size.y, size.z);
    const scale = 1.5 / maxDim;
    ref.current.scale.setScalar(scale);

    // Recompute scaled bounding box
    const scaledBox = new Box3().setFromObject(ref.current);
    const scaledCenter = new Vector3();
    const scaledSize = new Vector3();
    scaledBox.getCenter(scaledCenter);
    scaledBox.getSize(scaledSize);

    // Set OrbitControls target to model center
    if (controlsRef?.current) {
      controlsRef.current.target.copy(scaledCenter);
      controlsRef.current.update();
    }

    // Position camera to frame model
    const distance = Math.max(scaledSize.x, scaledSize.y, scaledSize.z) * 2.2;
    camera.position.set(
      scaledCenter.x,
      scaledCenter.y + distance * 0.4,
      scaledCenter.z + distance
    );
    camera.lookAt(scaledCenter);
  }, [camera, controlsRef]);

  return <primitive ref={ref} object={gltf.scene} />;
}

// ----------------------------
// Main LabModel component
export default function LabModel() {
  const [bgColor, setBgColor] = useState('#d1d5db'); // default light grey
  const controlsRef = useRef();

  // Idle auto-rotate timer
  const [idle, setIdle] = useState(true);
  let idleTimeout = useRef();

  // Reset idle timer on user interaction
  const resetIdle = () => {
    setIdle(false);
    clearTimeout(idleTimeout.current);
    idleTimeout.current = setTimeout(() => setIdle(true), 4000); // 4 sec idle
  };

  return (
    <div className="w-full rounded-xl overflow-hidden border border-gray-800">

      {/* Background color picker */}
      <div className="flex items-center gap-3 p-3 bg-gray-900 border-b border-gray-800">
        <span className="text-sm text-gray-300">Scene Background</span>
        <input
          type="color"
          value={bgColor}
          onChange={(e) => setBgColor(e.target.value)}
          className="w-8 h-8 cursor-pointer rounded"
        />
        <span className="text-xs text-gray-400">{bgColor}</span>
      </div>

      {/* 3D Canvas */}
      <div className="h-96">
        <Canvas
          camera={{ position: [0, 1.5, 3], fov: 50 }}
          gl={{ antialias: true }}
          onPointerDown={resetIdle} // reset idle on interaction
          onWheel={resetIdle}       // scroll zoom resets
          onPointerMove={resetIdle} // pointer move resets
        >
          <color attach="background" args={[bgColor]} />

          {/* Lights */}
          <ambientLight intensity={0.4} />
          <directionalLight position={[5, 5, 5]} intensity={1.2} />
          <directionalLight position={[-5, 3, -5]} intensity={0.6} />

          {/* Model */}
          <Suspense fallback={<Loading />}>
            <Model controlsRef={controlsRef} />
          </Suspense>

          {/* OrbitControls */}
          <OrbitControls
            ref={controlsRef}
            enableZoom
            enablePan={false}
            enableDamping
            dampingFactor={0.08}
            autoRotate={idle}        // auto rotate if idle
            autoRotateSpeed={0.3}    // smooth speed
          />
        </Canvas>
      </div>
    </div>
  );
}
