import React, { useState } from 'react';
import { Canvas, useFrame, useLoader } from '@react-three/fiber';
import { OrbitControls } from '@react-three/drei';
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader';
import { MeshStandardMaterial, BoxGeometry, Mesh } from 'three';

function Model({ path }) {
  const [error, setError] = useState(false);
  const gltf = useLoader(GLTFLoader, path, loader => {
    loader.manager.onError = () => setError(true);
  });

  if (error) {
    // fallback cube
    return (
      <mesh>
        <boxGeometry args={[1, 1, 1]} />
        <meshStandardMaterial color="red" />
      </mesh>
    );
  }

  return <primitive object={gltf.scene} scale={1} />;
}

export default function LabModel() {
  return (
    <Canvas style={{ width: '100%', height: '500px', background: '#111' }}>
      <ambientLight intensity={0.5} />
      <directionalLight position={[5, 5, 5]} />
      <Model path="/assets/models/lab-model.glb" />
      <OrbitControls />
    </Canvas>
  );
}
