import React, { useState, useRef, useEffect } from 'react';
import { motion, useAnimation } from 'framer-motion';

// Example logos array (replace with your actual supporter images)
const supporterLogos = [
  'https://via.placeholder.com/150x80?text=Logo1',
  'https://via.placeholder.com/150x80?text=Logo2',
  'https://via.placeholder.com/150x80?text=Logo3',
  'https://via.placeholder.com/150x80?text=Logo4',
  'https://via.placeholder.com/150x80?text=Logo5',
];

export default function SupportersCarousel({ speed = 50 }) {
  const [logos, setLogos] = useState([...supporterLogos, ...supporterLogos]); // duplicate for seamless loop
  const containerRef = useRef(null);
  const [containerWidth, setContainerWidth] = useState(0);
  const controls = useAnimation();

  useEffect(() => {
    if (containerRef.current) {
      setContainerWidth(containerRef.current.scrollWidth / 2);
    }

    controls.start({
      x: [-0, -containerWidth],
      transition: {
        x: {
          repeat: Infinity,
          repeatType: 'loop',
          duration: (containerWidth + 0) / speed,
          ease: 'linear',
        },
      },
    });
  }, [containerWidth, controls, speed]);

  return (
    <div className="w-full overflow-hidden">
      <motion.div
        ref={containerRef}
        className="flex gap-8"
        animate={controls}
        style={{ display: 'flex', whiteSpace: 'nowrap' }}
      >
        {logos.map((logo, index) => (
          <div key={index} className="flex-shrink-0">
            <img src={logo} alt={`Supporter ${index}`} className="h-20 object-contain" />
          </div>
        ))}
      </motion.div>
    </div>
  );
}
