import React, { Suspense, useState } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF } from '@react-three/drei';

const MODEL_URL = `${process.env.PUBLIC_URL}/assets/models/lab-model.glb`;

useGLTF.preload(MODEL_URL);

function Model() {
  const gltf = useGLTF(MODEL_URL);
  return <primitive object={gltf.scene} scale={0.6} />;
}

export default function LabModel() {
  const [bgColor, setBgColor] = useState('#d1d5db'); // light grey default

  return (
    <div className="w-full rounded-xl overflow-hidden border border-gray-800">

      {/* ðŸŽ¨ Color Picker UI */}
      <div className="flex items-center gap-3 p-3 bg-gray-900 border-b border-gray-800">
        <span className="text-sm text-gray-300">Scene Background</span>
        <input
          type="color"
          value={bgColor}
          onChange={(e) => setBgColor(e.target.value)}
          className="w-8 h-8 cursor-pointer rounded"
        />
        <span className="text-xs text-gray-400">{bgColor}</span>
      </div>

      {/* ðŸ§± 3D Canvas */}
      <div className="h-96">
        <Canvas camera={{ position: [0, 300, 100], fov: 50 }}>
          {/* Background color */}
          <color attach="background" args={[bgColor]} />

          {/* Lights */}
          <ambientLight intensity={0.4} />
          <directionalLight position={[5, 5, 5]} intensity={1.2} />
          <directionalLight position={[-5, 3, -5]} intensity={0.6} />

          {/* Model */}
          <Suspense fallback={<Loading />}>
            <Model />
          </Suspense>

          {/* Controls */}
          <OrbitControls enableZoom enablePan={false} />
        </Canvas>
      </div>
    </div>
  );
}

function Loading() {
  return (
    <mesh>
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial wireframe />
    </mesh>
  );
}
