import React, { useEffect, useState } from 'react';

export default function UnityLoader() {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const base = process.env.PUBLIC_URL || '';

    // --- PATCH SAFARI getContext BUG ---
    if (
      typeof HTMLCanvasElement !== 'undefined' &&
      !HTMLCanvasElement.prototype.getContextSafariWebGL2Fixed
    ) {
      const originalGetContext = HTMLCanvasElement.prototype.getContext;

      HTMLCanvasElement.prototype.getContextSafariWebGL2Fixed = function (
        type,
        attrs
      ) {
        // Call the original getContext, not itself!
        return originalGetContext.call(this, type, attrs);
      };
    }

    // --- LOAD UNITY ---
    const script = document.createElement('script');
    script.src = `${base}/assets/Build/assets.loader.js`;
    script.async = true;

    script.onload = () => {
      if (!window.createUnityInstance) {
        console.error(
          '❌ Unity loader loaded, but createUnityInstance not found'
        );
        return;
      }

      const unityContainer = document.querySelector('#unity-container');

      window.createUnityInstance(unityContainer, {
        dataUrl: `${base}/assets/Build/assets.data`,
        frameworkUrl: `${base}/assets/Build/assets.framework.js`,
        codeUrl: `${base}/assets/Build/assets.wasm`,
        streamingAssetsUrl: `${base}/StreamingAssets`,
        companyName: 'Iron Shift',
        productName: 'The Lab',
        productVersion: '1.0',
      },
      (progress) => {
        setProgress(progress);
        console.log('Loading Unity WebGL:', Math.round(progress * 100) + '%');
      }).then((instance) => {
        console.log('✅ Unity loaded successfully', instance);
      }).catch((err) => {
        console.error('❌ Error loading Unity:', err);
      });
    };

    script.onerror = () => {
      console.error('❌ Failed to load Unity loader script');
    };

    document.body.appendChild(script);

    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div style={{ width: '100%', height: '100vh', background: '#000' }}>
      <div
        id="unity-container"
        style={{ width: '100%', height: '100%' }}
      ></div>
      <div style={{ color: '#fff', position: 'absolute', top: 10, left: 10 }}>
        Loading: {Math.round(progress * 100)}%
      </div>
    </div>
  );
}
